async function googlesheet_plugin_001({ spreadsheetId, range, values, title, action }, context) {
  if (action === 'read') {
    const res = await fetch(`https://sheets.googleapis.com/v4/spreadsheets/${spreadsheetId}/values/${encodeURIComponent(range)}`, {
      headers: {
        Authorization: 'Bearer {{OAUTH_PLUGIN_ACCESS_TOKEN}}'
      }
    });
    return await res.json();

  } else if (action === 'write') {
    const res = await fetch(`https://sheets.googleapis.com/v4/spreadsheets/${spreadsheetId}/values/${encodeURIComponent(range)}?valueInputOption=RAW`, {
      method: 'PUT',
      headers: {
        Authorization: 'Bearer {{OAUTH_PLUGIN_ACCESS_TOKEN}}',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ values })
    });
    return await res.json();

  } else if (action === 'create') {
    if (!title) throw new Error('Missing title for new spreadsheet');
    const res = await fetch('https://sheets.googleapis.com/v4/spreadsheets', {
      method: 'POST',
      headers: {
        Authorization: 'Bearer {{OAUTH_PLUGIN_ACCESS_TOKEN}}',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ properties: { title } })
    });
    return await res.json();

  } else {
    throw new Error('Invalid action. Use "read", "write", or "create"');
  }
}
